		// camera = Camera.open(camId);
		// }*/
		int camId = Camera.CameraInfo.CAMERA_FACING_BACK;
		if (isFront) {
			camera = Camera.open(camId);
			camera.startPreview();

		} else {
			camera = Camera.open(camId + 1);
			camera.startPreview();

		}
		// startPreview();
	}

	@Override
	public void onPause() {
		if (inPreview) {
			camera.stopPreview();
		}

		camera.release();
		camera = null;
		inPreview = false;

		super.onPause();
	}

	private Camera.Size getBestPreviewSize(int width, int height,
			Camera.Parameters parameters) {
		Camera.Size result = null;

		for (Camera.Size size : parameters.getSupportedPreviewSizes()) {
			if (size.width &lt;= width &amp;&amp; size.height &lt;= height) {
				if (result == null) {
					result = size;
				} else {
					int resultArea = result.width * result.height;
					int newArea = size.width * size.height;

					if (newArea &gt; resultArea) {
						result = size;
					}
				}
			}
		}

		return (result);
	}

	private void initPreview(int width, int height) {
		if (camera != null &amp;&amp; previewHolder.getSurface() != null) {
			try {
				camera.setPreviewDisplay(previewHolder);
			} catch (Throwable t) {
				Log.e(&quot;PreviewDemo-surfaceCallback&quot;,
						&quot;Exception in setPreviewDisplay()&quot;, t);
				Toast.makeText(PreviewDemo.this, t.getMessage(),
						Toast.LENGTH_LONG).show();
			}

			if (!cameraConfigured) {
				Camera.Parameters parameters = camera.getParameters();
				Camera.Size size = getBestPreviewSize(width, height, parameters);

				if (size != null) {
					parameters.setPreviewSize(size.width, size.height);
					camera.setParameters(parameters);
					cameraConfigured = true;
				}
			}
		}
	}

	private void startPreview() {
		if (cameraConfigured &amp;&amp; camera != null) {

			camera.startPreview();
			inPreview = true;
		}
	}

	SurfaceHolder.Callback surfaceCallback = new SurfaceHolder.Callback() {
		public void surfaceCreated(SurfaceHolder holder) {
			// no-op -- wait until surfaceChanged()
		}

		public void surfaceChanged(SurfaceHolder holder, int format, int width,
				int height) {
			initPreview(width, height);
			startPreview();
		}

		public void surfaceDestroyed(SurfaceHolder holder) {
			// no-op
			if (camera != null) {
				/*
				 * Call stopPreview() to stop updating the preview surface.
				 */
				camera.stopPreview();

				/*
				 * Important: Call release() to release the camera for use by
				 * other applications. Applications should release the camera
				 * immediately in onPause() (and re-open() it in onResume()).
				 */
				camera.release();

				camera = null;
			}
		}
	};
    }']

