        self.positionPlayer = GLKVector2Add(self.positionPlayer, curRotate);     
        
        //Rotation Section
        self.effectPlayer.transform.modelviewMatrix = GLKMatrix4Rotate(self.effectPlayer.transform.modelviewMatrix, rotate, 0, 0, -1);
        
    }
    
